"""APIキー暗号化モジュール"""
import os
import base64
from typing import Optional

_encryption_key:Optional[bytes] = None


def _get_encryption_key()->bytes:
 global _encryption_key
 if _encryption_key is None:
  key_str = os.environ.get("ENCRYPTION_KEY")
  if key_str:
   _encryption_key = base64.urlsafe_b64decode(key_str.encode())
  else:
   _encryption_key = _generate_default_key()
 return _encryption_key


def _generate_default_key()->bytes:
 import hashlib
 seed = "toybox-default-encryption-key-2024"
 return hashlib.sha256(seed.encode()).digest()


def _get_fernet():
 try:
  from cryptography.fernet import Fernet
  key = _get_encryption_key()
  fernet_key = base64.urlsafe_b64encode(key[:32])
  return Fernet(fernet_key)
 except ImportError:
  return None


def encrypt_api_key(api_key:str)->str:
 fernet = _get_fernet()
 if fernet:
  encrypted = fernet.encrypt(api_key.encode())
  return base64.urlsafe_b64encode(encrypted).decode()
 return _simple_encrypt(api_key)


def decrypt_api_key(encrypted_key:str)->str:
 fernet = _get_fernet()
 if fernet:
  try:
   encrypted = base64.urlsafe_b64decode(encrypted_key.encode())
   decrypted = fernet.decrypt(encrypted)
   return decrypted.decode()
  except Exception:
   return _simple_decrypt(encrypted_key)
 return _simple_decrypt(encrypted_key)


def _simple_encrypt(text:str)->str:
 key = _get_encryption_key()
 result = []
 for i,char in enumerate(text):
  key_byte = key[i % len(key)]
  result.append(chr(ord(char) ^ key_byte))
 return base64.urlsafe_b64encode("".join(result).encode("utf-8","surrogateescape")).decode()


def _simple_decrypt(encrypted:str)->str:
 key = _get_encryption_key()
 try:
  decoded = base64.urlsafe_b64decode(encrypted.encode()).decode("utf-8","surrogateescape")
  result = []
  for i,char in enumerate(decoded):
   key_byte = key[i % len(key)]
   result.append(chr(ord(char) ^ key_byte))
  return "".join(result)
 except Exception:
  return ""


def generate_key_hint(api_key:str)->str:
 if len(api_key) <= 8:
  return "***"
 return f"{api_key[:3]}...{api_key[-3:]}"

"""APIキーリポジトリ"""
from typing import Optional,List,Dict,Any
from datetime import datetime
from sqlalchemy.orm import Session
from models.tables import ApiKeyStore
from security import encrypt_api_key,decrypt_api_key,generate_key_hint


class ApiKeyRepository:
 def __init__(self,session:Session):
  self.session = session

 def get(self,provider_id:str)->Optional[ApiKeyStore]:
  return self.session.query(ApiKeyStore).filter(
   ApiKeyStore.provider_id == provider_id
  ).first()

 def get_all(self)->List[ApiKeyStore]:
  return self.session.query(ApiKeyStore).all()

 def get_all_hints(self)->Dict[str,Dict[str,Any]]:
  keys = self.get_all()
  return {
   key.provider_id:{
    "hint":key.key_hint,
    "isValid":key.is_valid,
    "lastValidatedAt":key.last_validated_at.isoformat() if key.last_validated_at else None,
   }
   for key in keys
  }

 def save(self,provider_id:str,api_key:str)->ApiKeyStore:
  encrypted = encrypt_api_key(api_key)
  hint = generate_key_hint(api_key)
  existing = self.get(provider_id)
  if existing:
   existing.encrypted_key = encrypted
   existing.key_hint = hint
   existing.is_valid = False
   existing.updated_at = datetime.now()
   self.session.flush()
   return existing
  else:
   new_key = ApiKeyStore(
    provider_id=provider_id,
    encrypted_key=encrypted,
    key_hint=hint,
    is_valid=False,
   )
   self.session.add(new_key)
   self.session.flush()
   return new_key

 def delete(self,provider_id:str)->bool:
  existing = self.get(provider_id)
  if existing:
   self.session.delete(existing)
   self.session.flush()
   return True
  return False

 def get_decrypted_key(self,provider_id:str)->Optional[str]:
  key_store = self.get(provider_id)
  if key_store:
   return decrypt_api_key(key_store.encrypted_key)
  return None

 def update_validation_status(
  self,
  provider_id:str,
  is_valid:bool
 )->Optional[ApiKeyStore]:
  key_store = self.get(provider_id)
  if key_store:
   key_store.is_valid = is_valid
   key_store.last_validated_at = datetime.now()
   self.session.flush()
  return key_store

