"""AIプロバイダーヘルスモニター"""
import threading
import time
from typing import Dict,Optional,Callable,Any
from datetime import datetime
from .base import HealthCheckResult
from .registry import ProviderRegistry


class ProviderHealthMonitor:
 """プロバイダーのヘルス状態を定期監視"""
 _instance:Optional["ProviderHealthMonitor"] = None
 _lock = threading.Lock()

 def __new__(cls):
  if cls._instance is None:
   with cls._lock:
    if cls._instance is None:
     cls._instance = super().__new__(cls)
     cls._instance._initialized = False
  return cls._instance

 def __init__(self):
  if self._initialized:
   return
  self._health_states:Dict[str,HealthCheckResult] = {}
  self._check_interval = 10
  self._running = False
  self._thread:Optional[threading.Thread] = None
  self._on_health_change:Optional[Callable[[str,HealthCheckResult],None]] = None
  self._sio = None
  self._initialized = True

 def set_check_interval(self,seconds:int)->None:
  self._check_interval = max(5,seconds)

 def set_socketio(self,sio)->None:
  self._sio = sio

 def set_health_change_callback(self,callback:Callable[[str,HealthCheckResult],None])->None:
  self._on_health_change = callback

 def start(self)->None:
  if self._running:
   return
  self._running = True
  self._thread = threading.Thread(target=self._monitor_loop,daemon=True)
  self._thread.start()

 def stop(self)->None:
  self._running = False
  if self._thread:
   self._thread.join(timeout=2)
   self._thread = None

 def get_health_status(self,provider_id:str)->Optional[HealthCheckResult]:
  return self._health_states.get(provider_id)

 def get_all_health_status(self)->Dict[str,Dict[str,Any]]:
  return {
   pid:state.to_dict() for pid,state in self._health_states.items()
  }

 def check_provider_now(self,provider_id:str)->HealthCheckResult:
  provider = ProviderRegistry.get_fresh(provider_id)
  if not provider:
   result = HealthCheckResult(
    available=False,
    error=f"プロバイダーが見つかりません: {provider_id}",
    checked_at=datetime.now(),
   )
  elif not provider.validate_config():
   result = HealthCheckResult(
    available=False,
    error="APIキーが設定されていません",
    checked_at=datetime.now(),
   )
  else:
   result = provider.health_check()
  self._update_health_state(provider_id,result)
  return result

 def _monitor_loop(self)->None:
  while self._running:
   self._check_all_providers()
   time.sleep(self._check_interval)

 def _check_all_providers(self)->None:
  providers = ProviderRegistry.list_providers()
  for provider_info in providers:
   provider_id = provider_info["id"]
   if provider_id == "mock":
    continue
   try:
    self.check_provider_now(provider_id)
   except Exception as e:
    self._update_health_state(
     provider_id,
     HealthCheckResult(
      available=False,
      error=str(e),
      checked_at=datetime.now(),
     )
    )

 def _update_health_state(self,provider_id:str,result:HealthCheckResult)->None:
  previous = self._health_states.get(provider_id)
  self._health_states[provider_id] = result
  state_changed = (
   previous is None or
   previous.available != result.available
  )
  if state_changed:
   if self._on_health_change:
    self._on_health_change(provider_id,result)
   if self._sio:
    self._sio.emit("provider_health_changed",{
     "provider_id":provider_id,
     "health":result.to_dict(),
    })


def get_health_monitor()->ProviderHealthMonitor:
 return ProviderHealthMonitor()

"""Zhipu AI (智谱清言) プロバイダー"""
from typing import List,Optional,Dict,Any,Iterator
from .base import (
 AIProvider,AIProviderConfig,ChatMessage,ChatResponse,
 StreamChunk,ModelInfo,MessageRole
)


class ZhipuProvider(AIProvider):
 """Zhipu AIプロバイダー"""

 @property
 def provider_id(self)->str:
  return "zhipu"

 @property
 def display_name(self)->str:
  return "Zhipu AI (智谱清言)"

 def get_available_models(self)->List[ModelInfo]:
  return [
   ModelInfo(
    id="glm-4-plus",
    name="GLM-4 Plus",
    max_tokens=128000,
    supports_vision=False,
    supports_tools=True,
    input_cost_per_1k=0.05,
    output_cost_per_1k=0.05
   ),
   ModelInfo(
    id="glm-4-flash",
    name="GLM-4 Flash",
    max_tokens=128000,
    supports_vision=False,
    supports_tools=True,
    input_cost_per_1k=0.0001,
    output_cost_per_1k=0.0001
   ),
   ModelInfo(
    id="glm-4v-plus",
    name="GLM-4V Plus",
    max_tokens=8192,
    supports_vision=True,
    supports_tools=False,
    input_cost_per_1k=0.01,
    output_cost_per_1k=0.01
   ),
  ]

 def _get_client(self):
  if self._client is None:
   try:
    from zhipuai import ZhipuAI
    api_key = self.config.api_key
    if not api_key:
     from config import get_config
     app_config = get_config()
     api_key = getattr(app_config.agent,"zhipu_api_key",None)
    self._client = ZhipuAI(api_key=api_key)
   except ImportError:
    raise ImportError("zhipuaiパッケージがインストールされていません: pip install zhipuai")
  return self._client

 def _convert_messages(self,messages:List[ChatMessage])->List[Dict[str,str]]:
  converted = []
  for msg in messages:
   converted.append({"role":msg.role.value,"content":msg.content})
  return converted

 def chat(
  self,
  messages:List[ChatMessage],
  model:str,
  max_tokens:int = 1024,
  temperature:float = 0.7,
  **kwargs
 )->ChatResponse:
  client = self._get_client()
  msgs = self._convert_messages(messages)

  response = client.chat.completions.create(
   model=model,
   messages=msgs,
   max_tokens=max_tokens,
   temperature=temperature,
  )

  content = ""
  if response.choices:
   content = response.choices[0].message.content or ""

  return ChatResponse(
   content=content,
   model=response.model,
   input_tokens=response.usage.prompt_tokens if response.usage else 0,
   output_tokens=response.usage.completion_tokens if response.usage else 0,
   total_tokens=response.usage.total_tokens if response.usage else 0,
   finish_reason=response.choices[0].finish_reason if response.choices else None,
   raw_response=response
  )

 def chat_stream(
  self,
  messages:List[ChatMessage],
  model:str,
  max_tokens:int = 1024,
  temperature:float = 0.7,
  **kwargs
 )->Iterator[StreamChunk]:
  client = self._get_client()
  msgs = self._convert_messages(messages)

  response = client.chat.completions.create(
   model=model,
   messages=msgs,
   max_tokens=max_tokens,
   temperature=temperature,
   stream=True,
  )

  total_content = ""
  for chunk in response:
   if chunk.choices and chunk.choices[0].delta.content:
    content = chunk.choices[0].delta.content
    total_content += content
    yield StreamChunk(content=content)

  yield StreamChunk(
   content="",
   is_final=True,
  )

 def test_connection(self)->Dict[str,Any]:
  try:
   client = self._get_client()
   response = client.chat.completions.create(
    model="glm-4-flash",
    messages=[{"role":"user","content":"Hi"}],
    max_tokens=10,
   )
   return {
    "success":True,
    "message":"Zhipu AI: 正常に接続できました"
   }
  except Exception as e:
   error_str = str(e)
   if "api_key" in error_str.lower() or "auth" in error_str.lower():
    return {"success":False,"message":"認証エラー: APIキーが無効です"}
   return {"success":False,"message":f"エラー: {error_str}"}

 def validate_config(self)->bool:
  api_key = self.config.api_key
  if not api_key:
   try:
    from config import get_config
    app_config = get_config()
    api_key = getattr(app_config.agent,"zhipu_api_key",None)
   except:
    pass
  return bool(api_key)

