class AgentException(Exception):
 """エージェント例外の基底クラス"""
 def __init__(self,message:str,recoverable:bool = False):
  super().__init__(message)
  self.recoverable = recoverable


class ProviderUnavailableError(AgentException):
 """AIプロバイダーが利用不可"""
 def __init__(self,provider_id:str,reason:str = None):
  message = f"プロバイダー '{provider_id}' が利用できません"
  if reason:
   message += f": {reason}"
  super().__init__(message,recoverable=True)
  self.provider_id = provider_id
  self.reason = reason


class ContextValidationError(AgentException):
 """コンテキスト検証エラー"""
 def __init__(self,message:str,missing_fields:list = None):
  super().__init__(message,recoverable=False)
  self.missing_fields = missing_fields or []


class CheckpointTimeoutError(AgentException):
 """チェックポイント承認タイムアウト"""
 def __init__(self,checkpoint_id:str,timeout_seconds:float):
  message = f"チェックポイント '{checkpoint_id}' が {timeout_seconds}秒でタイムアウトしました"
  super().__init__(message,recoverable=True)
  self.checkpoint_id = checkpoint_id
  self.timeout_seconds = timeout_seconds


class QualityCheckFailedError(AgentException):
 """品質チェック失敗"""
 def __init__(self,agent_type:str,issues:list,retry_count:int):
  message = f"エージェント '{agent_type}' の品質チェックが {retry_count} 回失敗しました"
  super().__init__(message,recoverable=retry_count < 3)
  self.agent_type = agent_type
  self.issues = issues
  self.retry_count = retry_count


class MaxRetriesExceededError(AgentException):
 """最大リトライ回数超過"""
 def __init__(self,operation:str,max_retries:int):
  message = f"操作 '{operation}' が最大リトライ回数 ({max_retries}) を超えました"
  super().__init__(message,recoverable=False)
  self.operation = operation
  self.max_retries = max_retries


class RateLimitError(AgentException):
 """レート制限エラー"""
 def __init__(self,provider_id:str,retry_after:int = None):
  message = f"プロバイダー '{provider_id}' のレート制限に達しました"
  if retry_after:
   message += f" ({retry_after}秒後に再試行可能)"
  super().__init__(message,recoverable=True)
  self.provider_id = provider_id
  self.retry_after = retry_after

"""リトライ戦略モジュール"""
import asyncio
import random
from typing import Callable,TypeVar,Optional,Set
from dataclasses import dataclass,field
from .exceptions import (
 AgentException,
 ProviderUnavailableError,
 RateLimitError,
 MaxRetriesExceededError,
)

T = TypeVar("T")


@dataclass
class RetryConfig:
 max_retries:int = 3
 base_delay:float = 1.0
 max_delay:float = 60.0
 exponential_base:float = 2.0
 jitter:bool = True
 jitter_factor:float = 0.5


RETRYABLE_EXCEPTIONS:Set[type] = {
 ProviderUnavailableError,
 RateLimitError,
 ConnectionError,
 TimeoutError,
}


def is_retryable_error(error:Exception)->bool:
 if isinstance(error,AgentException):
  return error.recoverable
 for exc_type in RETRYABLE_EXCEPTIONS:
  if isinstance(error,exc_type):
   return True
 error_str = str(error).lower()
 retryable_patterns = [
  "rate limit",
  "timeout",
  "connection",
  "temporarily unavailable",
  "service unavailable",
  "503",
  "429",
  "overloaded",
 ]
 return any(pattern in error_str for pattern in retryable_patterns)


def calculate_delay(
 attempt:int,
 config:RetryConfig,
 retry_after:Optional[int] = None
)->float:
 if retry_after:
  return float(retry_after)
 delay = config.base_delay * (config.exponential_base ** attempt)
 delay = min(delay,config.max_delay)
 if config.jitter:
  jitter_range = delay * config.jitter_factor
  delay = delay + random.uniform(-jitter_range,jitter_range)
  delay = max(0.1,delay)
 return delay


async def retry_with_backoff(
 operation:Callable[...,T],
 operation_name:str = "operation",
 config:Optional[RetryConfig] = None,
 on_retry:Optional[Callable[[int,Exception,float],None]] = None,
 *args,
 **kwargs
)->T:
 config = config or RetryConfig()
 last_error:Optional[Exception] = None
 for attempt in range(config.max_retries + 1):
  try:
   result = await operation(*args,**kwargs)
   return result
  except Exception as e:
   last_error = e
   if not is_retryable_error(e):
    raise
   if attempt >= config.max_retries:
    raise MaxRetriesExceededError(operation_name,config.max_retries) from e
   retry_after = None
   if isinstance(e,RateLimitError):
    retry_after = e.retry_after
   delay = calculate_delay(attempt,config,retry_after)
   if on_retry:
    on_retry(attempt + 1,e,delay)
   await asyncio.sleep(delay)
 raise MaxRetriesExceededError(operation_name,config.max_retries) from last_error


def retry_sync_with_backoff(
 operation:Callable[...,T],
 operation_name:str = "operation",
 config:Optional[RetryConfig] = None,
 on_retry:Optional[Callable[[int,Exception,float],None]] = None,
 *args,
 **kwargs
)->T:
 import time
 config = config or RetryConfig()
 last_error:Optional[Exception] = None
 for attempt in range(config.max_retries + 1):
  try:
   result = operation(*args,**kwargs)
   return result
  except Exception as e:
   last_error = e
   if not is_retryable_error(e):
    raise
   if attempt >= config.max_retries:
    raise MaxRetriesExceededError(operation_name,config.max_retries) from e
   retry_after = None
   if isinstance(e,RateLimitError):
    retry_after = e.retry_after
   delay = calculate_delay(attempt,config,retry_after)
   if on_retry:
    on_retry(attempt + 1,e,delay)
   time.sleep(delay)
 raise MaxRetriesExceededError(operation_name,config.max_retries) from last_error


class RetryContext:
 """リトライコンテキスト（状態追跡用）"""
 def __init__(self,config:Optional[RetryConfig] = None):
  self.config = config or RetryConfig()
  self.attempt = 0
  self.total_delay = 0.0
  self.errors:list = []

 def record_error(self,error:Exception,delay:float)->None:
  self.errors.append({
   "attempt":self.attempt,
   "error":str(error),
   "error_type":type(error).__name__,
   "delay":delay,
  })
  self.attempt += 1
  self.total_delay += delay

 def should_retry(self)->bool:
  return self.attempt < self.config.max_retries

 def get_summary(self)->dict:
  return {
   "total_attempts":self.attempt,
   "total_delay_seconds":self.total_delay,
   "errors":[e["error_type"] for e in self.errors],
  }

