import {test,expect} from '@playwright/test'

test.describe('ProjectView - シナリオ1: 新規プロジェクト作成',()=>{
 test.beforeEach(async({page})=>{
  await page.goto('/')
 })

 test('新規プロジェクトを作成できる',async({page})=>{
  // 1. 「+」ボタンをクリックしてフォームを表示
  await page.click('button[title="新規作成"]')

  // 2. 「新規プロジェクト作成」フォームが表示されることを確認
  await expect(page.locator('text=新規プロジェクト作成')).toBeVisible()

  // 3. 必須項目を入力
  const projectName='E2Eテストプロジェクト_'+Date.now()
  await page.fill('input[placeholder*="例: NebulaForge"]',projectName)
  await page.fill('textarea[placeholder*="作りたいゲームのアイデア"]','2D横スクロールアクションRPG。宇宙を舞台にしたSF世界観。')

  // 4. プラットフォーム選択 (PC)
  await page.click('button:has-text("PC")')

  // 5. スコープ選択 (Indies)
  await page.click('button:has-text("Indies")')

  // 6. 「作成」ボタンが有効化されていることを確認
  const createButton=page.locator('button:has-text("作成")').first()
  await expect(createButton).toBeEnabled()

  // 7. 「作成」ボタンをクリック
  await createButton.click()

  // 8. 結果確認: プロジェクト一覧に追加され、選択状態になる
  await expect(page.locator(`text=${projectName}`).first()).toBeVisible({timeout:10000})

  // 9. 右パネルに「プロジェクト詳細」が表示される
  await expect(page.locator('text=プロジェクト詳細')).toBeVisible()

  // 10. ステータスが「下書き」であることを確認
  await expect(page.locator('text=下書き').first()).toBeVisible()
 })

 test('必須項目が空の場合、作成ボタンが無効',async({page})=>{
  await page.click('button[title="新規作成"]')
  await expect(page.locator('text=新規プロジェクト作成')).toBeVisible()

  // 何も入力しない状態で作成ボタンが無効であることを確認
  const createButton=page.locator('button:has-text("作成")').first()
  await expect(createButton).toBeDisabled()

  // プロジェクト名のみ入力
  await page.fill('input[placeholder*="例: NebulaForge"]','テスト')
  await expect(createButton).toBeDisabled()

  // ゲームアイデアも入力すると有効化
  await page.fill('textarea[placeholder*="作りたいゲームのアイデア"]','テストアイデア')
  await expect(createButton).toBeEnabled()
 })

 test('キャンセルボタンでフォームが閉じる',async({page})=>{
  await page.click('button[title="新規作成"]')
  await expect(page.locator('text=新規プロジェクト作成')).toBeVisible()

  // キャンセルをクリック
  await page.click('button:has-text("キャンセル")')

  // フォームが閉じて、「プロジェクトを選択するか」メッセージが表示される
  await expect(page.locator('text=プロジェクトを選択するか、新規作成してください')).toBeVisible()
 })

 test('右パネルの「新規プロジェクト作成」ボタンからもフォームを開ける',async({page})=>{
  // 右パネルのボタンをクリック
  const rightPanelButton=page.locator('button:has-text("新規プロジェクト作成")')
  if(await rightPanelButton.isVisible()){
   await rightPanelButton.click()
   await expect(page.locator('text=新規プロジェクト作成').first()).toBeVisible()
  }
 })
})

