import {test,expect,Page} from '@playwright/test'

const createTestProject=async(page:Page,suffix:string)=>{
 await page.click('button[title="新規作成"]')
 const name=`E2E_${suffix}_${Date.now()}`
 await page.fill('input[placeholder*="例: NebulaForge"]',name)
 await page.fill('textarea[placeholder*="作りたいゲームのアイデア"]','テスト用アイデア')
 await page.click('button:has-text("作成")').catch(()=>page.locator('button:has-text("作成")').first().click())
 await expect(page.locator(`text=${name}`).first()).toBeVisible({timeout:10000})
 return name
}

