import {test,expect,Page} from '@playwright/test'

const setupProject=async(page:Page)=>{
 await page.goto('/')
 await page.click('button[title="新規作成"]')
 const name='E2E_checkpoint_'+Date.now()
 await page.fill('input[placeholder*="例: NebulaForge"]',name)
 await page.fill('textarea[placeholder*="作りたいゲームのアイデア"]','テスト用')
 await page.locator('button:has-text("作成")').first().click()
 await expect(page.locator(`text=${name}`).first()).toBeVisible({timeout:10000})
 return name
}

const navigateToCheckpoints=async(page:Page)=>{
 await page.click('text=Checkpoints')
 await expect(page.locator('text=チェックポイント').first()).toBeVisible({timeout:5000})
}

test.describe('CheckpointsView - 承認管理',()=>{
 test.describe('シナリオ1: チェックポイント一覧表示',()=>{
  test('Checkpointsタブをクリックして一覧が表示される',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToCheckpoints(page)
   await expect(page.locator('text=チェックポイント').first()).toBeVisible()
  })

  test('チェックポイントがない場合は空メッセージが表示される',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToCheckpoints(page)
   // チェックポイントがなければ空状態
   const emptyMsg=page.locator('text=チェックポイントがありません')
   const list=page.locator('[data-testid="checkpoint-list"]')
   await expect(emptyMsg.or(list)).toBeVisible({timeout:5000})
  })
 })

 test.describe('シナリオ2: チェックポイント詳細表示',()=>{
  test.skip('チェックポイントカードをクリックして詳細が表示される',async({page})=>{
   // チェックポイントが存在する状態が前提
   await page.goto('/')
   await setupProject(page)
   await page.click('button:has-text("開始")')
   await page.waitForTimeout(5000) // エージェント実行待ち
   await navigateToCheckpoints(page)

   const card=page.locator('[data-testid="checkpoint-card"]').first()
   if(await card.isVisible()){
    await card.click()
    await expect(page.locator('text=CHECKPOINT')).toBeVisible()
   }
  })
