def get_supported_agents(self)->List[AgentType]:
    result = []
    for agent_str in self.SUPPORTED_AGENTS:
        try:
            result.append(AgentType(agent_str))
        except ValueError:
            pass

    for agent_type in AgentType:
        if agent_type not in result:
            result.append(agent_type)

    return result

