import {test,expect,Page} from '@playwright/test'

const setupProject=async(page:Page)=>{
 await page.goto('/')
 await page.click('button[title="新規作成"]')
 const name='E2E_data_'+Date.now()
 await page.fill('input[placeholder*="例: NebulaForge"]',name)
 await page.fill('textarea[placeholder*="作りたいゲームのアイデア"]','テスト用')
 await page.locator('button:has-text("作成")').first().click()
 await expect(page.locator(`text=${name}`).first()).toBeVisible({timeout:10000})
 return name
}

const navigateToData=async(page:Page)=>{
 await page.click('text=Data')
 await expect(page.locator('text=アセット').first()).toBeVisible({timeout:5000})
}

test.describe('DataView - アセット管理',()=>{
 test.describe('シナリオ1: アセット一覧表示',()=>{
  test('Dataタブをクリックして一覧が表示される',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToData(page)
   // フィルタパネルが表示される
   await expect(page.locator('text=タイプ').first()).toBeVisible()
  })

  test('アセットがない場合は空状態が表示される',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToData(page)
   const emptyMsg=page.locator('text=アセットがありません')
   const grid=page.locator('[data-testid="asset-grid"]')
   await expect(emptyMsg.or(grid)).toBeVisible({timeout:5000})
  })
 })

 test.describe('シナリオ2: タイプでフィルタ',()=>{
  test('「全て」フィルタが選択可能',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToData(page)
   const allFilter=page.locator('text=全て').first()
   await expect(allFilter).toBeVisible()
  })

  test('「画像」フィルタが選択可能',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToData(page)
   const imageFilter=page.locator('text=画像').first()
   if(await imageFilter.isVisible()){
    await imageFilter.click()
   }
  })

  test('「音声」フィルタが選択可能',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToData(page)
   const audioFilter=page.locator('text=音声').first()
