    await audioFilter.click()
   }
  })

  test('「ドキュメント」フィルタが選択可能',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToData(page)
   const docFilter=page.locator('text=ドキュメント').first()
   if(await docFilter.isVisible()){
    await docFilter.click()
   }
  })

  test('「コード」フィルタが選択可能',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToData(page)
   const codeFilter=page.locator('text=コード').first()
   if(await codeFilter.isVisible()){
    await codeFilter.click()
   }
  })
 })

