  test('「全状態」フィルタが選択可能',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToData(page)
   const filter=page.locator('text=全状態').first()
   if(await filter.isVisible()){
    await filter.click()
   }
  })

  test('「未承認」フィルタがデフォルト選択',async({page})=>{
   await page.goto('/')
   await setupProject(page)
   await navigateToData(page)
