 test.describe('シナリオ7: アセット詳細表示',()=>{
  test.skip('アセットカードをクリックして詳細モーダルが表示される',async({page})=>{
   // アセットが存在する状態が前提
   await page.goto('/')
   const card=page.locator('[data-testid="asset-card"]').first()
   if(await card.isVisible()){
    await card.click()
    await expect(page.locator('text=ダウンロード')).toBeVisible()
   }
  })

  test.skip('モーダルの×ボタンで閉じられる',async({page})=>{
   await page.goto('/')
   const card=page.locator('[data-testid="asset-card"]').first()
   if(await card.isVisible()){
    await card.click()
    await page.locator('button[aria-label="閉じる"]').click()
    await expect(page.locator('[role="dialog"]')).not.toBeVisible()
   }
  })

  test.skip('モーダルの背景クリックで閉じられる',async({page})=>{
   await page.goto('/')
   const card=page.locator('[data-testid="asset-card"]').first()
