    await card.click()
    await page.locator('[data-testid="modal-overlay"]').click({position:{x:10,y:10}})
    await expect(page.locator('[role="dialog"]')).not.toBeVisible()
   }
  })
 })

 test.describe('シナリオ8: 音声再生',()=>{
  test.skip('音声アセットの再生ボタンで再生できる',async({page})=>{
